package dia2205;

public class usandoForEach {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String [] names = new String[3];
		names[0] = "lisa";
		names[1] = "kevin";
		names[2] = "roger";
		
		for(String name : names) {
			System.out.print(name + ", ");
		}

	}

}
